function showRowEditForm(e,t){e.children("tbody").children("tr").removeClass("editing");if(t!==null){t.addClass("editing");t.next("tr.form").addClass("editing")}}function toggleDetails(e){if($("#details"+e+":visible").length){$("#btn"+e).attr("src","i/+.png");$("#details"+e).slideUp()}else{$("#btn"+e).attr("src","i/-.png");$("#details"+e).slideDown()}}function initTables(){$("table.edit>tbody>tr.row>td").children("a.edit,.btn.edit").click(function(){showRowEditForm($(this).closest("table.edit"),$(this).closest("tr.row"))});$("table.edit>tbody>tr.form .close").click(function(){showRowEditForm($(this).closest("table.edit"),null)});$("table.collapsable a.more").click(function(e){e.preventDefault();toggleDetails($(this).data("more"))})}$(document).ready(function(){initTables()});